type Inf = {
    logourl: string;
  };
  
  export default function Info() {
    const { navigate } = useNavigation();
    const route = useRoute<nome_prop>();
    const { nome_acao } = route.params;
  
    const [acao_encontrada, set_acao_encontrada] = useState<Inf | null>(null);
  
    const procurar_acoes = async (url: string) => {
      try {
        const resposta = await fetch(url);
        if (!resposta.ok) {
          throw new Error("Falha ao carregar dados da API");
        }
        const dados = await resposta.json();
        // Verifique se os dados são válidos antes de atualizar o estado
        if (dados.results && dados.results.length > 0) {
          set_acao_encontrada(dados.results[0]);
        } else {
          throw new Error("Dados inválidos retornados da API");
        }
      } catch (error) {
        console.error(error);
        // Lidar com o erro, por exemplo, mostrando uma mensagem de erro
        set_acao_encontrada(null); // Defina como null para não tentar renderizar a imagem
      }
    };
  
    useEffect(() => {
      const url_pesquisa_acao = `https://brapi.dev/api/quote/${nome_acao}?range=1d&interval=1d&fundamental=true&dividends=true`;
      console.log(url_pesquisa_acao);
      procurar_acoes(url_pesquisa_acao);
    }, [nome_acao]);
  
    return (
      <View style={styles.container}>
        <View style={styles.container2}>
          <View style={styles.container3}>
            <Text style={styles.text}>PETER FINANCE</Text>
          </View>
        </View>
        <View style={styles.container_inf_nome}>
          {acao_encontrada && acao_encontrada.logourl ? (
            <Image
              source={{ uri: acao_encontrada.logourl }}
              style={styles.imagem}
            />
          ) : (
            <Text>Carregando...</Text>
          )}
          <View>
            {/* Outros elementos aqui */}
          </View>
        </View>
        <Button
          title="voltar"
          onPress={() => {
            navigate('Home');
          }}
        />
      </View>
    );
  }
